import itemBg from "../Components/BattlePass/BattlePassBg.vue";
import CdnResolverImage from "../../../../Components/CdnResolverImage.vue";
export default {
  data() {
    return {
      bindRarity: {
        1: "#828282",
        2: "#34c924",
        3: "#2bd9d9",
        4: "#a30eff",
        5: "#ff5533",
        6: "#ffcc4d"
      }
    };
  },
  components: {
    itemBg,
    CdnResolverImage
  },
  methods: {
    actionPrize(id, isTake) {
      if (isTake) {
        this.$callClient("C:Prize:Take", id);
        return;
      }
      this.$callClient("C:Prize:Sell", id);
    },
    getItemImageDefault(item) {
      if (item.Clothes) {
        let index = -1;
        switch (item.Type) {
          case "Hat":
            index = -12;
            break;
          case "Top":
            index = -11;
            break;
          case "Leg":
            index = -4;
            break;
          case "Feet":
            index = -6;
            break;
          case "Accessories":
            index = -14;
            break;
          case "Mask":
            index = -1;
            break;
          case "Jewelry":
            index = -7;
            break;
          case "Glasses":
            index = -13;
            break;
          case "Backs":
            index = -20;
            break;
        }
        if (item.Type == "Backs" || item.Type == "Mask") {
          return `cdn/inventory/clothes/${index}/${item.background}`;
        }
        return `cdn/inventory/clothes/${index}/${item.Gender ? "True" : "False"}/${item.background}`;
      }
      if (item.Type == "ArmorSkin") {
        return `cdn/inventory/clothes/-9/False/${item.background}`;
      }
      if (item.Type === "WeaponDraw" || item.Type === "WeaponSticker") {
        return `cdn/inventory/clothes/weaponskins/${item.background}`;
      }
      if (item.Type == "Car") {
        return `cdn/help/battlepass/caseitems/${item.background}`;
      }
      if (item.Type == "Item") {
        return `cdn/inventory/other/${item.background}`;
      }
      if (item.Type == "Animation") {
        return `https://cdn.nexus-rp.net/animations/${item.background}`;
      }
      if (item.Type == "Wheels") {
        return `cdn/wheels/${item.background}`;
      }
      return `cdn/inventory/other/${item.background}`;
    },
    onMouseenter(ref) {
      setTimeout(() => {
        this.$refs[ref][0].play();
      });
    },
    onMouseleave(ref) {
      setTimeout(() => {
        this.$refs[ref][0].pause();
      });
    }
  },
  computed: {
    getPrizeList() {
      return this.$store.getters["playerMenu/getPrizeList"];
    }
  },
  mounted() {
    // console.log(this.getPrizeList);
    // setTimeout(() => {
    //   this.$store.dispatch("playerMenu/RemovePrize", 0);
    // }, 1500);
  }
};
