import TeamItem from "./Assets/teamItemIcon.vue";
export default {
  components: {
    TeamItem
  },
  data() {
    return {
      playerList: [{
        name: "BIba Drop",
        id: "456",
        health: 45
      }],
      buttonInfo: {
        keyCode: 32,
        description: "Нажмите «SPACE», чтобы выпрыгнуть",
        eventName: "ENEVT"
      },
      isShowButton: false,
      phaseData: null,
      // {
      //   ZoneProgress: 50,
      //   PlayerProgress: 30,
      //   zoneName: "Фаза №1",
      //   Time: `3:46`,
      // },
      statsData: {
        survivor: 50,
        kills: 5
      }
    };
  },
  methods: {
    getStyle(player) {
      return {
        width: player.health + "%"
      };
    },
    setStatsData(data) {
      this.statsData = data;
    },
    getKeyByKeyCode(keyCode) {
      const key = String.fromCharCode(keyCode);
      const bindKeyCode = {
        32: "space"
      };
      return !key ? key : bindKeyCode[keyCode];
    },
    setPhaseData(data) {
      this.phaseData = data;
    },
    toggleIsShowBtn(value) {
      this.isShowButton = value;
    },
    onkeydown(e) {
      if (!this.isShowButton) {
        return;
      }
      if (e.keyCode === this.buttonInfo.keyCode) {
        this.$callClient(`C:BattleRoyale:${this.buttonInfo.eventName}`);
      }
    }
  },
  mounted() {
    this.$onClient("C:BattleRoyale:SetStatsData", this.setStatsData);
    this.$onClient("C:BattleRoyale:SetPhaseData", this.setPhaseData);
    this.$onClient("C:BattleRoyale:ToggleIsShowBtn", this.toggleIsShowBtn);
    this.$callClient("C:BattleRoyale:Ready");
    window.addEventListener("keyup", this.onkeydown);
  },
  beforeUnmount() {
    this.$offClient("C:BattleRoyale:SetStatsData", this.setStatsData);
    this.$offClient("C:BattleRoyale:SetPhaseData", this.setPhaseData);
    this.$offClient("C:BattleRoyale:ToggleIsShowBtn", this.toggleIsShowBtn);
    window.removeEventListener("keyup", this.onkeydown);
  }
};
