import MPInput from "../../MP-Input/MP-Input.vue";
import MPActionBtn from "../../MP-ActionBtn/MP-ActionBtn.vue";
export default {
  props: {
    modalData: {
      type: Object,
      required: true
    },
    modalType: {
      type: String,
      required: true
    }
  },
  data() {
    return {
      reason: ""
    };
  },
  methods: {
    onClose() {
      this.$store.commit("marketPlace/closeModal");
    },
    setReason(value) {
      this.reason = value;
    },
    sendComplaint() {
      this.$callClient("C:MarketPlace:Listing:Complain", this.modalData.id, this.reason);
      this.$store.commit("marketPlace/complainListing", this.modalData.id);
      this.onClose();
    }
  },
  computed: {
    getSelectedItem() {
      return this.$store.getters["marketPlace/getSelectedItem"];
    }
  },
  components: {
    MPActionBtn,
    MPInput
  }
};
