import MPInput from "../../MP-Input/MP-Input.vue";
import MPActionBtn from "../../MP-ActionBtn/MP-ActionBtn.vue";
import InfoBlock from "./InfoBlock.vue";
import { formatNumber } from "../../../Assets/functions";
export default {
  props: {
    modalData: {
      type: Object,
      required: true
    }
  },
  data() {
    return {
      payMethods: [{
        title: "Готівкою",
        value: "cash"
      }, {
        title: "Банківською картою",
        value: "card"
      }],
      pickedQuantity: 0
    };
  },
  computed: {
    getModalData() {
      return [[{
        title: "Назва товару",
        value: this.modalData.name
      }], [{
        title: "Продавець",
        value: this.modalData.holder,
        static: `#${this.modalData.static}`
      }], [{
        title: "Доступна кількість",
        value: `${this.modalData.count} ${this.$t("шт.")}`
      }, {
        title: "Вартість продажу за 1 шт.",
        value: `${formatNumber(this.modalData.priceOfCount, " ")} $`
      }, {
        title: "Таймер лота",
        value: this.modalData?.timer || ""
      }]];
    },
    getSelectedItem() {
      return this.$store.getters["marketPlace/getSelectedItem"];
    },
    getQuantityAmount() {
      return formatNumber(this.pickedQuantity * this.modalData.priceOfCount, " ");
    }
  },
  methods: {
    setPickedQuantity(quantity) {
      this.pickedQuantity = quantity;
    },
    onClose() {
      this.$store.commit("marketPlace/closeModal");
    },
    buy(method) {
      if (this.pickedQuantity && method) {
        console.log(this.modalData.id);
        this.$callClient("C:MarketPlace:Exchange:Buy", this.getSelectedItem.id, this.modalData.id, this.pickedQuantity, method);
        this.onClose();
      }
    }
  },
  components: {
    MPActionBtn,
    MPInput,
    InfoBlock
  }
};
