export default {
  props: {
    propertyData: {
      type: Object,
      required: true
    },
    lvl: {
      type: Number,
      required: true
    }
  },
  components: {},
  data() {
    return {
      scale: 1
    };
  },
  methods: {
    setScaleIconProcent() {
      const fontSize = getComputedStyle(document.documentElement).fontSize;
      const size = Number(fontSize.slice(0, -2)) / 10;
      this.scale = size;
    },
    getStrokeDashoffset(count, max) {
      if (count >= max) return 0;
      const procent = count / max * 100;
      return 100 - procent;
    },
    setViewBox() {
      const svg = this.$refs.svg;
      const {
        x,
        y,
        width,
        height
      } = svg.getBBox();
      svg.setAttribute("viewBox", `${x} ${y} ${width} ${height}`);
    }
  },
  computed: {
    isFull() {
      return this.lvl >= this.propertyData.max;
    }
  },
  mounted() {
    window.addEventListener("resize", this.setScaleIconProcent);
    this.setScaleIconProcent();
  },
  beforeUnmount() {
    window.removeEventListener("resize", this.setScaleIconProcent);
  }
};
