export default {
  data: function () {
    return {
      wantedActive: false,
      locale: "ua",
      offender: "Ame_Maranse",
      articles: "",
      partner: "",
      hoverStars: 0,
      activeStarts: 1,
      comment: "",
      redemption: false
    };
  },
  methods: {
    showStar(i) {
      return i <= this.activeStarts ? true : false;
    },
    setStar(i) {
      this.activeStarts = i;
    },
    showHover(i) {
      return i <= this.hoverStars ? true : false;
    },
    setHover(i) {
      if (i !== undefined) this.hoverStars = i;
    },
    arrestCrime() {
      this.$callClient("wantedMenu", JSON.stringify({
        offender: this.offender,
        articles: this.articles,
        partner: this.partner,
        stars: this.activeStarts.toString(),
        comment: this.comment,
        redemption: this.redemption.toString()
      }));
    },
    closeApp() {
      this.wantedActive = false;
      this.$callClient("wantedClose");
    },
    setData(data) {
      this.$recursiveAssign(data, this.$data);
    }
  },
  mounted() {
    this.wantedActive = true;
    this.$onClient("C:wantedMenu:SetData", this.setData);
    this.$callClient("W:wantedMenu:Ready");
    window.addEventListener("mouseup", e => {
      if (e.key === "Escape") this.closeApp();
    });
  },
  beforeUnmount() {
    this.$offClient("C:wantedMenu:SetData", this.setData);
    window.removeEventListener("mouseup", e => {
      if (e.key === "Escape") this.closeApp();
    });
  },
  watch: {
    articles: function (val) {
      if (!/^[0-9.,]+$/.test(val)) {
        this.articles = val.substring(0, val.length - 1);
      }
    },
    partner: function (val) {
      if (!/^[a-zA-Z_]+$/.test(val)) {
        this.partner = val.substring(0, val.length - 1);
      }
    }
  }
};
