export default {
  data() {
    return {
      currentImgPath: "",
      isLoaded: true
    };
  },
  props: {
    img: {
      type: String,
      required: true
    }
  },
  methods: {
    getImgPath(img) {
      const localUrl = new Image();
      localUrl.src = img.localUrl;
      console.log(localUrl);
      localUrl.onload = () => {
        console.log("load");
        this.currentImgPath = img.localUrl;
      };
      localUrl.onerror = () => {
        console.log("error");
        this.currentImgPath = img.url;
        if (!this.currentImgPath) {
          this.isLoaded = false;
        }
      };
    }
  },
  mounted() {
    this.getImgPath(this.img);
  }
};
