import { getItemCardImgPath, getIsImgFull } from "@/Application/Pages/MarketPlace/Assets/functions.js";
import CdnResolverImage from "@/Components/CdnResolverImage.vue";
export default {
  props: {
    itemCard: {
      type: Number,
      required: false
    },
    imgs: {
      type: Array,
      required: true
    }
  },
  components: {
    CdnResolverImage
  },
  data() {
    return {
      selectedOptionIdx: null,
      imgsList: []
    };
  },
  methods: {
    onOption(optionIdx) {
      this.selectedOptionIdx = optionIdx;
      this.setImg();
    },
    setImg() {
      if (this.imgsList[this.selectedOptionIdx] === "default") {
        this.$emit("pick", "");
      } else {
        this.$emit("pick", `https://cdn.nexus-rp.net/MarketPlaceServer/public/images/${this.itemCard.id}_${this.imgsList[this.selectedOptionIdx]}.png`);
      }
    },
    getImgPath(img) {
      if (img === "default" && this.itemCard.objectType !== "service") {
        return getItemCardImgPath(this.itemCard);
      } else if (img === "default" && this.itemCard.objectType === "service") {
        return require("@/Application/Pages/MarketPlace/Components/MP-ItemCard/Assets/Img/createListing.png");
      } else {
        return `https://cdn.nexus-rp.net/MarketPlaceServer/public/images/${this.itemCard.id}_${img}.png`;
      }
    },
    getIsImgFull() {
      return getIsImgFull(this.itemCard.objectType);
    }
  },
  mounted() {
    this.selectedOptionIdx = 0;
    this.imgsList = ["default", ...this.imgs];
  }
};
