export default {
  template: `
    <div class="controls-navigation" >
        <div class="contorols-navigation__tab" v-for="tab in tabs" :class="{active:isActive(tab)}" @click="setActive(tab)">
            <div class="controls-navigation__tab-image" v-html="getImage(tab)"></div>
            <p v-html="$t(getTitle(tab))"></p>
        </div>
    </div>
    `,
  props: {
    tabs: {
      type: Array
    },
    route: {
      type: String,
      default: "members"
    },
    Svgs: {
      type: Object
    },
    callback: {
      type: String,
      required: true
    }
  },
  methods: {
    getTitle(tab) {
      return tab && tab.title !== undefined ? tab.title : ``;
    },
    getImage(tab) {
      return tab && tab.route !== undefined && this.Svgs[tab.route] !== undefined ? this.Svgs[tab.route] : ``;
    },
    isActive(tab) {
      return tab && tab.route === this.route ? true : false;
    },
    setActive(tab) {
      if (tab && tab.route !== undefined && tab.route !== this.route) this.$EBus.$emit(this.callback, tab.route);
    }
  }
};
