import BgEffect from "../../../Components/Skins/Bg-Effect.vue";
import CdnResolverImage from "../../../../../../Components/CdnResolverImage.vue";
export default {
  data() {
    return {
      typesList: {
        0: "crime",
        1: "gov"
      },
      selectedType: "все"
    };
  },
  computed: {
    filteredArmorList() {
      if (this.selectedType === "все") {
        return this.ArmorList;
      }
      return this.ArmorList.filter(item => item.type === this.selectedType);
    },
    ArmorList() {
      return this.$store.getters["playerMenu/getDrawList"];
    }
  },
  methods: {
    setArmor(id) {
      this.$callClient("C:Draw:Set", id);
    },
    removeArmor(id) {
      this.$callClient("C:Draw:Remove", id);
    },
    toInventory(id) {
      this.$callClient("C:Draw:ToInventory", id);
    },
    getImgPath(name) {
      return `cdn/inventory/clothes/weaponskins/${name}.png`;
    }
  },
  components: {
    BgEffect,
    CdnResolverImage
  }
};
