import Handcuffs from "../../Assets/Icons/Handcuffs.vue";
import Star from "../../Assets/Icons/Star.vue";
export default {
  components: {
    Handcuffs,
    Star
  },
  data() {
    return {
      name: "",
      stars: 0,
      newStars: 0,
      hoverStars: 0,
      articles: ""
    };
  },
  methods: {
    setParams(name, stars) {
      this.name = name;
      this.stars = stars;
      this.newStars = stars;
    },
    closeModal() {
      this.$emit("closeModal");
    },
    showHover(i) {
      return i <= this.hoverStars;
    },
    showStar(i) {
      return i <= this.newStars;
    },
    setStar(i) {
      this.newStars = i;
    },
    setHover(i) {
      if (i !== undefined) this.hoverStars = i;
    },
    isAdded(i) {
      return i > this.stars && i <= this.newStars;
    },
    isRemoved(i) {
      return i <= this.stars && i > this.newStars;
    },
    setStars() {
      this.$RPC.call("Tablet:SetStars", this.name, this.newStars, this.articles).then(() => {}).catch(() => {}).finally(() => this.closeModal());
    }
  },
  watch: {
    articles: function (val) {
      if (!/^[0-9.,]+$/.test(val)) {
        this.articles = val.substring(0, val.length - 1);
      }
    }
  },
  created() {
    this.$EBus.on("Tablet.PoliceFBI->ArrestModal:SetParams", this.setParams);
    this.$EBus.emit("Tablet.ArrestModal->PoliceFBI:Mounted");
  },
  beforeUnmount() {
    this.$EBus.off("Tablet.PoliceFBI->ArrestModal:SetParams", this.setParams);
  }
};
