import ActionBtn from "../../../../MP-ActionBtn/MP-ActionBtn.vue";
export default {
  components: {
    ActionBtn
  },
  methods: {
    addOffer() {
      if (this.getItemCardForSell) {
        this.$store.commit("marketPlace/openModal", [this.getItemCardForSell, "exchangeAdd"]);
      }
    },
    focusMyOffers() {
      this.$store.commit("marketPlace/switchFocusExchangeOffers");
    }
  },
  computed: {
    getItemCardForSell() {
      if (this.getStockItemsCard.length) {
        const itemCard = this.getStockItemsCard.find(itemCard => itemCard.itemID === this.getSelectedItem.itemID);
        return itemCard || null;
      }
      return null;
    },
    getMyOffersLength() {
      if (this.getSelectedItem?.data?.buyInfo) {
        return this.getSelectedItem.data.buyInfo.filter(offer => offer.static === this.getStatic).length;
      } else {
        return 0;
      }
    },
    getStockItemsCard() {
      return this.$store.getters["marketPlace/getCardsList"]("stock");
    },
    getSelectedItem() {
      return this.$store.getters["marketPlace/getSelectedItem"];
    },
    getStatic() {
      return this.$store.getters["playerMenu/getStatic"];
    }
  }
};
