export default {
  props: {
    title: {
      type: String,
      required: true
    },
    placeholder: {
      type: String,
      default: ""
    },
    value: {
      type: [String, Number],
      required: true
    }
  },
  data() {
    return {
      textWidth: 0
    };
  },
  computed: {
    getMaskStyle() {
      return `linear-gradient(transparent 0.1rem, #FFFFFF 0.1rem, #FFFFFF), linear-gradient(90deg, #FFFFFF 2rem, transparent 2rem, transparent ${3.6 + this.textWidth}rem, #FFFFFF ${3.6 + this.textWidth}rem)`;
    }
  },
  methods: {
    initBorder() {
      this.textWidth = this.$refs.text.clientWidth / this.$fontSize;
    },
    setFocus() {
      this.$refs.input.focus();
    },
    onInput(e) {
      e.stopPropagation();
      this.$emit("input", e);
    },
    onFocus(e) {
      e.stopPropagation();
      this.$emit("focus", e);
    },
    onBlur(e) {
      e.stopPropagation();
      this.$emit("blur", e);
    }
  },
  mounted() {
    this.initBorder();
  },
  watch: {
    "$i18n.locale": function () {
      this.initBorder();
    }
  }
};
