import { checkSection } from "@/Application/Pages/MarketPlace/Assets/functions";
import MPPrice from "../../../MP-Price/MP-Price.vue";
export default {
  components: {
    MPPrice
  },
  props: {
    itemCard: {
      type: Object,
      required: true
    }
  },
  methods: {
    checkSection(path) {
      return checkSection(this.$route, path);
    }
  },
  computed: {
    getItemsCount() {
      if (this.itemCard && this.itemCard.data && this.itemCard.data.buyInfo) {
        return this.itemCard.data.buyInfo.reduce((total, item) => total + item.count, 0);
      }
      return 0;
    },
    getRentType() {
      if (this.itemCard.data?.rentType) {
        const types = {
          hours: "/ час",
          days: "/ день"
        };
        return types[this.itemCard.data?.rentType] || "";
      } else {
        return "";
      }
    }
  }
};
