export default {
  data() {
    return {
      listOfCategories: [],
      selectedType: "",
      description: "",
      typeList: [{
        name: "Недвижимость",
        typeId: "Estate",
        type: "house"
      }, {
        name: "Работа",
        typeId: "Work",
        type: "helmet"
      }, {
        name: "Бизнес",
        typeId: "Bussines",
        type: "diplomat"
      }, {
        name: "Услуги",
        typeId: "Service",
        type: "instruments"
      }, {
        name: "Знакомства",
        typeId: "Dating",
        type: "human"
      }, {
        name: "Автомобиль",
        typeId: "Car",
        type: "car"
      }, {
        name: "Скидки",
        typeId: "Sale",
        type: "label"
      }, {
        name: "Другое",
        typeId: "Other",
        type: "other"
      }]
    };
  },
  computed: {
    isNotAccess() {
      return this.description.length < 10 || !this.selectedType;
    }
  },
  methods: {
    onCancel() {
      this.$router.go(-1);
    },
    addNotify() {
      this.$store.dispatch("phone/addNotify", {
        title: "Успешно",
        text: "Объявление успешно создано",
        type: "success"
      });
    },
    onSubmit() {
      if (this.isNotAccess) return;
      this.$store.dispatch("phone/addModal", {
        title: "Подтвердите действие",
        text: "Вы действительно хотите отправить новость?",
        buttons: [{
          text: "Отменить",
          action: () => {
            this.$store.dispatch("phone/removeModal");
          }
        }, {
          text: "Подтвердить",
          action: () => {
            this.$callClient("W:Phone:Weazel:New", {
              typeId: this.selectedType,
              description: this.description
            });
            this.onCancel();
            this.$store.dispatch("phone/removeModal");
          }
        }]
      });
    }
  }
};
