import CdnResolverImage from "@/Components/CdnResolverImage.vue";
export default {
  data() {
    return {
      days: 5,
      money: 5000,
      endTime: 0,
      interval: null
    };
  },
  computed: {
    dailyPrizeData() {
      return this.$store.getters["playerMenu/getDailyPrizeData"];
    },
    getFormattedTime() {
      let hours = Math.floor(this.endTime / 3600);
      let minutes = Math.floor(this.endTime % 3600 / 60);
      return [hours, minutes];
    }
  },
  methods: {
    GetImagePath(item, isModal) {
      if (item.image.includes("cdn/")) return item.image;
      if (item.ObjectType == "AnimationItemPrize") {
        return `animations/nexus_animations_2_chicken_wing.webm`;
      }
      if (item.ObjectType == "ClothesItemPrize") {
        let index = -1;
        switch (item.ItemType) {
          case "Hat":
            index = -12;
            break;
          case "Top":
            index = -11;
            break;
          case "Leg":
            index = -4;
            break;
          case "Feet":
            index = -6;
            break;
          case "Accessories":
            index = -14;
            break;
          case "Mask":
            index = -1;
            break;
          case "Jewelry":
            index = -7;
            break;
          case "Glasses":
            index = -13;
            break;
          case "Backs":
            index = -20;
            break;
          case "BodyArmor":
            index = -9;
            break;
        }
        console.log(`cdn/inventory/clothes/${index}/${item.image}${item.variation}_${item.colors[isModal ? this.colorIndex : 0]}.png`);
        return `cdn/inventory/clothes/${index}/${item.image}${item.variation}_${item.colors[isModal ? this.colorIndex : 0]}.png`;
      }
      return `cdn/inventory/other/${item.image}.png`;
    },
    take(day, status) {
      if (status !== "available") {
        return;
      }
      this.$callClient("C:PlayerMenu:TakeDailyPrize", day);
    }
  },
  components: {
    CdnResolverImage
  },
  mounted() {
    this.endTime = this.dailyPrizeData.EndTime - Math.round(new Date().getTime() / 1000.0);
    this.interval = setInterval(() => {
      this.endTime = this.dailyPrizeData.EndTime - Math.round(new Date().getTime() / 1000.0);
    }, 1000);
  },
  beforeUnmount() {
    clearInterval(this.interval);
  }
};
