import { mapGetters } from "vuex";
export default {
  data() {
    return {
      active: false
    };
  },
  computed: {
    ...mapGetters("global", ["getCdnResolver", "getModuleWrapper"])
  },
  methods: {
    keyHandler(e) {
      console.log("KeyHandler");
      if (e.keyCode === 89) {
        this.action(true);
        console.log("esfsdf");
      }
      if (e.keyCode === 78) {
        this.action(false);
      }
    },
    action(result) {
      console.log("action");
      this.$RPC.call("ModuleWrapper.DecisionNotification.Action", result).then(([status]) => {
        if (status) {
          this.active = false;
        }
      });
    },
    closeHandler() {
      this.$store.commit("global/updateModuleWrapper", [{
        decisionNotification: {
          status: false,
          data: null,
          animation: {
            name: "decision-notification-fade-animation"
          }
        }
      }]);
    }
  },
  created() {
    console.log("Created");
    document.addEventListener("keyup", this.keyHandler);
  },
  beforeUnmount() {
    console.log("Unmount");
    document.removeEventListener("keyup", this.keyHandler);
  },
  mounted() {
    this.active = true;
    let audio = new Audio(`${this.getCdnResolver.cdnLink}/audio/moduleWrapper/decisionNotification/notification-1.mp3`);
    audio.volume = 0.5;
    audio.play();
  }
};
