const Resizer = {
  fontSize: 10,
  onResize() {
    let {
      innerWidth,
      innerHeight
    } = window;
    let xZoom = innerWidth / 1600;
    let yZoom = innerHeight / 900;
    let fontSize = (xZoom < yZoom ? xZoom : yZoom) * 10;
    Resizer.fontSize = fontSize;
    document.querySelector("html").style.fontSize = `${fontSize.toFixed(2)}px`;
  }
};
window.addEventListener("resize", Resizer.onResize);
Resizer.onResize();
export default {
  install: app => {
    app.config.globalProperties.$fontSize = Resizer.fontSize;
  }
};
