      const ActivateCount = this.PMData.RefSystem.ActivateCount;
      const LvlData = this.PMData.RefSystem.LvlData[i].value;
      const priviusLvlData = this.PMData.RefSystem.LvlData[i - 1]?.value || 0;
      const rangeValue = (ActivateCount - priviusLvlData) / (LvlData - priviusLvlData) * 100;
      if (ActivateCount >= LvlData || LvlData == undefined && priviusLvlData <= ActivateCount) {
