    onCopy(name) {
      const elem = this.$refs[name];
      elem.select();
      document.execCommand("copy");
      if (elem.setSelectionRange) {
        // Выделяем весь текст в поле ввода
        elem.setSelectionRange(0, elem.value.length);
      } else if (elem.createTextRange) {
        // Для поддержки старых версий Internet Explorer
        var range = elem.createTextRange();
        range.collapse(true);
        range.moveEnd("character", elem.value.length);
        range.moveStart("character", 0);
        range.select();
      } else if (elem.selectionStart) {
        // Для поддержки браузеров, поддерживающих selectionStart
        elem.selectionStart = 0;
        elem.selectionEnd = elem.value.length;
      }
      this.$Notification.create({
        type: "info",
        layout: "compact-bottom__right",
        theme: "compact",
        time: 2000,
        text: `
                    <div class="notification-text__item">
                        <div class="notification-text__item-title">
                            <span>${this.$t("Текст скопирован")}</span>
                        </div>
                    </div>
                    <div class="notification-text__item">
                        <div class="notification-text__item-icon">
                            <span class="icon-info"></span>
                        </div>
                    </div>
                `,
        progressBar: false
      });
    },
