      };
      img.onerror = () => {
        this.socialImgPath = require(`../Assets/StatistcIcons/Avatar.png`);
      };
      img.src = imageUrl;
    },
    calculatingRangeInnerWidth(value, maxValue) {
      let rangeValue = value;
      const maxWidth = 21;
      if (value > maxValue) {
        rangeValue = maxValue;
      }
      const procent = rangeValue / maxValue;
      return procent * maxWidth + "rem";
    },
    convertToMoneyFormat(numb) {
      return new Intl.NumberFormat("de-DE").format(numb) + "$";
    },
    formatMoney(amount) {
