export function formatNumber(num, separator) {
  if (!num) return 0;
  return num.toFixed(0).toString().replace(/\B(?=(\d{3})+(?!\d))/g, separator);
}
export function getItemCardImgPath(itemCard) {
  if (itemCard.objectType === "business") {
    return `cdn/marketplace/business/${itemCard.bizID}.png`;
  } else if (itemCard.objectType === "car") {
    return `cdn/phone/transport/${itemCard.model}.png`;
  } else if (itemCard.objectType === "item") {
    return `cdn/inventory/other/${itemCard.itemID}.png`;
  } else if (itemCard.objectType === "clothes") {
    let index = -1;
    const background = itemCard.variation + "_" + itemCard.texture;
    switch (itemCard.clothesType) {
      case "Hat":
        index = -12;
        break;
      case "Top":
        index = -11;
        break;
      case "Leg":
        index = -4;
        break;
      case "Feet":
        index = -6;
        break;
      case "Accessories":
        index = -14;
        break;
      case "Mask":
        index = -1;
        break;
      case "Jewelry":
        index = -7;
        break;
      case "Glasses":
        index = -13;
        break;
      case "Backs":
        index = -20;
        break;
    }
    if (itemCard.clothesType == "Backs" || itemCard.clothesType == "Mask") {
      return `cdn/inventory/clothes/${index}/${background}.png`;
    }
    return `cdn/inventory/clothes/${index}/${itemCard.gender ? "True" : "False"}/${background}.png`;
  } else if (itemCard.objectType === "house") {
    return `cdn/marketplace/houses/${itemCard.houseID}.png`;
  } else if (itemCard.objectType === "apart") {
    return `cdn/marketplace/apartments/${itemCard.houseID}.png`;
  } else {
