import InfoIcon from "../../../../Components/Icons/PhoneApp/InfoIcon.vue";
import { setContactInfoItem } from "../../../../Lib/additionalUiFunctions";
import PhoneMixin from "@/Application/Pages/Phone/Mixins/PhoneMixin.vue";
export default {
  components: {
    InfoIcon
  },
  props: {
    callItem: {
      type: Object,
      required: true
    },
    isFavorites: {
      type: Boolean,
      default: false
    }
  },
  mixins: [PhoneMixin],
  methods: {
    onClickInfo() {
      setContactInfoItem(this.callItem);
    },
    onContactClick() {
      this.$callClient("C:Phone:CallNumber", this.callItem.number);
    },
    getTimeSince(date) {
      const now = new Date();
      const past = new Date(date);
      const nowDate = now.getDate();
      const pastDate = past.getDate();
      const daysOfWeek = ["Воскресенье", "Понедельник", "Вторник", "Среда", "Четверг", "Пятница", "Суббота"];
      if (nowDate === pastDate) {
        return past.toLocaleTimeString("it-IT", {
          hour: "2-digit",
          minute: "2-digit"
        });
      } else if (nowDate - pastDate === 1) {
        return "Вчера";
      } else if (nowDate - pastDate > 1 && nowDate - pastDate < 7) {
        return daysOfWeek[past.getDay()];
      } else {
        return `${pastDate}.${("0" + (past.getMonth() + 1)).slice(-2)}.${past.getFullYear()}`;
      }
    }
  },
  mounted() {
    console.log(this.callItem);
  }
};
