import { mapGetters } from "vuex";
import IconDagger1 from "../../assets/icons/dagger-1.vue";
import IconCart1 from "../../assets/icons/cart-1.vue";
import "../../style/global/modal/top-up-balance.scss";
export default {
  components: {
    IconDagger1,
    IconCart1
  },
  data() {
    return {
      inputs: {
        amount: {
          value: "",
          valid: false,
          handler: {
            validate: e => {
              let regExp = /^[\d]+$/;
              this.inputs.amount.valid = regExp.test(e.target.value);
              if (!this.inputs.amount.valid) {
                return this.inputs.amount.value = this.inputs.amount.value.slice(0, this.inputs.amount.value.length - 1);
              }
              this.getPayment.amount = Number(this.inputs.amount.value);
            }
          }
        }
      }
    };
  },
  computed: {
    ...mapGetters("global", ["getCdnResolver", "getPayment", "getModals"])
  },
  methods: {
    modalTopUpBalanceCloseEscape(e) {
      if (e.keyCode === 27 && !window.isModalShow) {
        this.$emit("modalCloseEscape");
      }
    },
    modalTopUpBalanceCloseDagger() {
      this.$emit("modalCloseDagger");
    },
    modalTopUpBalanceFormSubmit(e) {
      this.$emit("modalFormSubmit", e);
    }
  },
  created() {
    document.addEventListener("keyup", this.modalTopUpBalanceCloseEscape);
  },
  beforeUnmount() {
    document.removeEventListener("keyup", this.modalTopUpBalanceCloseEscape);
  }
};
