import { MapNames, WeaponNames } from "../Components/ArenaEnums.js";
export default {
  props: {
    list: {
      type: Array,
      required: true
    },
    active: {
      required: true
    }
  },
  data() {
    return {
      isSelection: false
    };
  },
  methods: {
    openSelection() {
      this.isSelection = !this.isSelection;
    },
    selectItem(item) {
      this.$emit("select", item);
      this.isSelection = false;
    },
    getMapName(map) {
      return MapNames[map] || "";
    },
    getWeaponName(weapon) {
      return WeaponNames[weapon] || "";
    }
  }
};
