export default {
  el: "#app",
  data: function () {
    return {
      appActive: false,
      locale: "ua",
      dNumber: ``,
      dName: ``,
      dPrice: ``,
      dTax: ``,
      dOwner: ``,
      dRoof: ``,
      canBuy: false,
      isOwner: false,
      openIn: false
    };
  },
  methods: {
    moneyString(e) {
      return e ? `${e.toLocaleString("uk-UA")} $` : "";
    },
    showBusiness(props) {
      if (props) {
        this.dNumber = props.number && typeof props.number === "number" ? `${this.$t("Бизнес")} №${props.number}` : ``;
        this.dName = props.name ? `${this.$t("Информация о бизнесе")} ${props.name}` : ``;
        this.dPrice = props.price && typeof props.price === "number" ? `${this.$t("Стоимость")}: ${this.moneyString(props.price)}` : ``;
        this.dTax = props.tax && typeof props.tax === "number" ? `${this.$t("Налог")}: ${this.moneyString(props.tax)} / ${this.$t("сутки")}` : ``;
        if (props.owner) {
          this.dOwner = `${this.$t("Владелец")}: ${props.owner == "Аукцион" ? this.$t("Аукцион") : props.owner}`;
          this.canBuy = false;
        } else {
          this.dOwner = `${this.$t("Владелец")}: ${this.$t("государство")}`;
          this.canBuy = true;
        }
        this.dRoof = props.roof ? `${this.$t("Крыша")}: ${props.roof}` : `${this.$t("Крыша")}: ${this.$t("отсутствует")}`;
        if (props.isOwner) this.isOwner = props.isOwner;
      }
      this.appActive = true;
    },
    buyBusiness() {
      this.$callClient("C:Business:buyBusiness");
      this.closeBusiness();
    },
    closeBusiness() {
      this.appActive = false;
    },
    openInfo() {
      this.openIn = true;
      this.appActive = false;
    },
    destroyBrowser() {
      if (this.openIn) this.$callClient("C:Business:GopenInfo");
      this.$callClient("C:Business:destroyBrowser");
    },
    onKeyUp(e) {
      if (e.code === "Escape") this.closeBusiness();
    }
  },
  mounted() {
    window.addEventListener("keyup", this.onKeyUp);
    this.$onClient("W:Business:showBusiness", this.showBusiness);
    this.$callClient("C:Business:Info:Ready");
  },
  beforeUnmount() {
    window.removeEventListener("keyup", this.onKeyUp);
    this.$offClient("W:Business:showBusiness", this.showBusiness);
  }
};
