import "core-js/modules/es.array.push.js";
import Header from "./Components/Header.vue";
export default {
  components: {
    Header
  },
  data() {
    return {
      time: 0,
      hints: [{
        keys: ["Esc"],
        title: "Закрыть окно"
      }],
      history: {}
    };
  },
  provide() {
    return {
      history: this.$store.getters["playerMenu/getBankHistory"]
    };
  },
  computed: {
    isBack() {
      return !this.$route.fullPath.includes("/atm/home");
    },
    getFormattedTime() {
      return this.time ? new Date(this.time).toLocaleTimeString("uk-UA", {
        hour: "numeric",
        minute: "numeric"
      }) : "";
    }
  },
  methods: {
    onTimer(state = true) {
      if (state) {
        this.time = Date.now();
        this.timeout = setTimeout(this.onTimer, 1000 * (60 - new Date(this.time).getSeconds()));
      } else if (this.timeout !== undefined) {
        clearTimeout(this.timeout);
      }
    },
    onKeyUp(e) {
      if (e.keyCode === 27 && !window.isModalShow) this.$router.push("/");
    }
  },
  created() {
    window.addEventListener("keyup", this.onKeyUp);
    this.onTimer(true);
    this.$callClient("C:ATM:Ready");
  },
  beforeUnmount() {
    window.removeEventListener("keyup", this.onKeyUp);
    this.onTimer(false);
    this.$callClient("C:ATM:Destroyed");
  }
};
