    </div>
    `,
  props: {
    vehicle: {
      type: Object,
      required: true
    }
  },
  computed: {
    displayID() {
      return this.vehicle && this.vehicle.id ? this.vehicle.id : "";
    },
    displayDate() {
      return this.vehicle && this.vehicle.date ? this.vehicle.date : "";
    },
    displayTime() {
      return this.vehicle && this.vehicle.time ? this.vehicle.time : "";
    },
    displayName() {
      return this.vehicle && this.vehicle.name ? this.vehicle.name : "";
    },
    displayNumber() {
      return this.vehicle && this.vehicle.number ? `(${this.vehicle.number})` : "(Отсутсвует)";
    },
    displayDriver() {
      return this.vehicle && this.vehicle.driver ? `За рулем: ${this.vehicle.driver}` : "";
    },
    displayRank() {
      return this.vehicle && this.vehicle.rankName ? this.vehicle.rankName : "";
    }
  },
  methods: {
    trackDownVehicle() {
      this.$EBus.$emit("src/components/controls/organization/vehicles/default/VehiclesItem->src/mixins/controls/organization/Vehicles:VehiclesTrackDownVehicle", this.vehicle.id);
    },
    changeCarAccess() {
      this.$EBus.$emit("src/components/controls/organization/vehicles/default/VehiclesItem->src/mixins/controls/organization/Vehicles:VehiclesChangeVehicleAccess", this.vehicle.id);
    }
  }
};
