import SystemAppHeader from "../../../../Components/SystemAppHeader.vue";
export default {
  data() {
    return {
      notifyValue: 0.5,
      conversationValue: 0.5,
      min: 0,
      max: 1
    };
  },
  computed: {
    getSettingsData() {
      return this.$store.getters["phone/getSettingsData"];
    },
    isChanged() {
      return this.notifyValue !== this.getSettingsData.soundSettings.notifyValue || this.conversationValue !== this.getSettingsData.soundSettings.conversationValue;
    }
  },
  methods: {
    getGradient(current) {
      const value = (current - this.min) / (this.max - this.min) * 100;
      return `linear-gradient(to right, #0A84FF 0%, #0A84FF ${value}%, #1C1C1E ${value}%, #1C1C1E 100%)`;
    },
    onSave() {
      this.$store.commit("phone/setSoundSettings", {
        notifyValue: this.notifyValue,
        conversationValue: this.conversationValue
      });
      this.$callClient("W:Phone:Settings:Save", "soundSettings", {
        notifyValue: this.notifyValue,
        conversationValue: this.conversationValue
      });
    }
  },
  mounted() {
    this.notifyValue = this.getSettingsData.soundSettings.notifyValue;
    this.conversationValue = this.getSettingsData.soundSettings.conversationValue;
  },
  components: {
    SystemAppHeader
  }
};
