export default {
  props: {
    recipeItem: Object,
    haveCount: Number,
    count: Number
  },
  components: {
    CdnResolverImage
  },
  methods: {
    GetImagePath(itemId) {
      return `cdn/inventory/other/${itemId}.png`;
    }
  },
  computed: {
    getClassName() {
      if (this.recipeItem?.Count * this.count <= this.haveCount) {
        return "enough";
      } else if (!this.haveCount) return "no-enough";
      return "";
    }
  }
