import SystemAppHeader from "../../../../Components/SystemAppHeader.vue";
import PhoneMixin from "../../../../Mixins/PhoneMixin";
export default {
  data() {
    return {
      playerRingtone: null,
      curretnRingtone: null,
      link: "",
      ringtoneList: [{
        name: "default",
        src: "default"
      }]
    };
  },
  watch: {
    link() {
      const audio = new Audio(this.link);
      audio.onloadeddata = () => {
        this.curretnRingtone = this.link;
        console.log("loaded");
      };
    }
  },
  computed: {
    wallpaperStyle() {
      return `url(${this.link}) no-repeat center/cover`;
    }
  },
  methods: {
    playRingtone() {
      if (this.playerRingtone) {
        this.playerRingtone.pause();
      }
      this.playerRingtone = new Audio(this.link);
      this.playerRingtone.play();
    },
    onSave() {
      let link = this.link;
      if (!this.link.trim()) {
        link = "default";
      }
      this.$callClient("W:Phone:Settings:Save", "wallpaper", link);
      this.$store.commit("phone/setPhoneSetting", {
        wallpaper: link
      });
      this.$router.go(-1);
    }
  },
  mixins: [PhoneMixin],
  components: {
    SystemAppHeader
  }
};
