import "core-js/modules/es.array.push.js";
import BuyChips from "./Components/Icons/BuyChips.vue";
import SellChips from "./Components/Icons/SellChips.vue";
export default {
  components: {
    BuyChips,
    SellChips
  },
  data() {
    return {
      hints: [{
        keys: ["Esc"],
        title: "Закрыть окно"
      }],
      tabs: [{
        path: "/chips/buy",
        name: "Купить фишки",
        image: "buy"
      }, {
        path: "/chips/sell",
        name: "Продать фишки",
        image: "sell"
      }],
      data: {
        balance: 0,
        chips: 0,
        buyPrice: 0,
        sellPrice: 0
      }
    };
  },
  computed: {
    currentPrice() {
      return this.$route.fullPath.includes("sell") ? this.data.sellPrice : this.data.buyPrice;
    }
  },
  methods: {
    onKeyUp(e) {
      if (e.keyCode === 27 && !window.isModalShow) this.$router.push("/");
    },
    setData(data) {
      this.data = data;
    }
  },
  created() {
    this.$onClient("W:CasinoShop:SetData", this.setData);
    window.addEventListener("keyup", this.onKeyUp);
    this.$callClient("C:CasinoShop:Ready");
  },
  beforeUnmount() {
    this.$offClient("W:CasinoShop:SetData", this.setData);
    window.addEventListener("keyup", this.onKeyUp);
    this.$callClient("C:CasinoShop:Destroyed");
  }
};
