      this.isShowNewModal = true;
    },
    onCloseNewModal() {
      this.isShowNewModal = false;
    },
    getGradient(current) {
      const value = (current - this.Settings.MinPlayers) / (this.Settings.MaxPlayers - this.Settings.MinPlayers) * 100;
      return `linear-gradient(to right, #40BF6A 0%, #40BF6A ${value}%, #151D1E ${value}%, #151D1E 100%)`;
    },
