      const data = {
        MaxPlayers: this.playerCount,
        AllowedBullets: this.selectedBulletType,
        AllowedItems: this.Settings.ItemList.filter(item => item.value).map(item => item.id)
      };
      this.$callClient("Gang:StartCapture", data);
      this.isShowNewModal = false;
    },
    getKey(it) {
      return it ? it.t + it.s : "";
    },
    setData(data, type) {
      this.type = type;
      this.title = data.t;
      this.itemdata = data.r;
      this.loaded = true;
      this.Settings = data.Settings;
    },
    GangCapture() {
      this.isShowNewModal = true;
    },
    SelectButton(data) {
      this.$callClient("Gang:CameraRotator", data);
    },
    getComponent(key) {
      return this.$options.components[key] !== undefined ? this.$options.components[key] : false;
    },
    AppClose() {
      this.$callClient("Farm:DestroyBrowser");
    },
    onKeyUp(e) {
      if (e.keyCode === 27 && !window.isModalShow) {
        // Escape
        this.AppClose();
      }
    }
  },
  mounted() {
    window.addEventListener("keyup", this.onKeyUp);
    this.$onClient("W:FarmInfo:SetData", this.setData);
    this.$callClient("W:FarmInfo:BrowserReady");
  },
  beforeUnmount() {
    window.removeEventListener("keyup", this.onKeyUp);
    this.$offClient("W:FarmInfo:SetData", this.setData);
  }
