import Search from "../../Assets/Icons/Search.vue";
export default {
  components: {
    Search
  },
  props: {
    pattern: {
      type: RegExp
    },
    placeholder: {
      type: String,
      require: true
    }
  },
  data() {
    return {
      input: ""
    };
  },
  methods: {
    search() {
      if (this.input !== "") this.$emit("search", this.input);
    }
  },
  watch: {
    input: function (val) {
      if (this.pattern && !this.pattern.test(val)) this.input = val.substring(0, val.length - 1);
      this.$emit("input");
    }
  }
};
