import LogsHeader from "./LogsHeader.js";
import LogsRow from "./LogsRow.js";
import Pages from "../../global/organization/MainPages.js";
export default {
  template: `
    <div class="controls-main__wrapper header pages">
        <LogsHeader
            :logsFound="logsFound">
        </LogsHeader>
        <div class="controls-main__content">
            <transition-group name="controls-members-list" tag="div" class="controls-main__content-list">
                <LogsRow 
                    v-for="log in logs"
                    :key="getLogKey(log)"
                    :log="getLogProps(log)">
                </LogsRow>
            </transition-group>
            <Pages
                v-if="showPages"
                :page = "logsPage"
                :max-pages="logsPagesCount"
                @setPage="setPage">
            </Pages>
        </div>
    </div>
    `,
  components: {
    LogsHeader,
    LogsRow,
    Pages
  },
  props: {
    logs: {
      type: Array,
      required: true
    },
    logsFound: {
      type: Number,
      required: true
    },
    logsPage: {
      type: Number,
      required: true
    },
    logsPagesCount: {
      type: Number,
      required: true
    }
  },
  computed: {
    showPages() {
      return this.logsPagesCount && this.logsPagesCount !== undefined && this.logsPagesCount > 1 ? true : false;
    }
  },
  methods: {
    getLogKey(log) {
      return log && log.id !== undefined ? log.id : 0;
    },
    getLogProps(log) {
      return log !== undefined ? log : {};
    },
    setPage(type) {
      this.$EBus.$emit("src/components/controls/organization/logs/Logs->src/mixins/controls/organization/Logs:LogsSetPage", type);
    }
  }
};
