import BlacklistFooter from "./BlacklistFooter.js";
import BlacklistHeader from "./BlacklistHeader.js";
import BlacklistRow from "./BlacklistRow.js";
export default {
  template: `
    <div class="controls-main__wrapper header pages">
        <BlacklistHeader 
            :membersFound="membersFound"
            @goBack="goBack">
        </BlacklistHeader>
        <div class="controls-main__content">
            <transition-group name="controls-members-list" tag="div" class="controls-main__content-list">
                <BlacklistRow v-for="member in members"
                    :key="getKey(member)"
                    :member="member">
                </BlacklistRow>
            </transition-group>
            <BlacklistFooter
                @setPage="setPage"
                @setType="setType"
                :page="membersPage"
                :maxPages="membersPagesCount"
                :type="blacklistType">
            </BlacklistFooter>
        </div>
    </div>
    `,
  components: {
    BlacklistHeader,
    BlacklistRow,
    BlacklistFooter
  },
  props: {
    members: {
      type: Array,
      required: true
    },
    membersFound: {
      type: Number,
      required: true
    },
    membersPage: {
      type: Number,
      required: true
    },
    membersPagesCount: {
      type: Number,
      required: true
    },
    blacklistType: {
      type: String,
      required: true
    }
  },
  methods: {
    getKey(member) {
      return member && member.id !== undefined ? member.id : 0;
    },
    goBack() {
      this.$EBus.$emit("src/components/controls/organization/action/blacklist/ActionsBlackList->src/mixins/controls/organization/Actions:goBack");
    },
    setPage(type) {
      this.$EBus.$emit("src/components/controls/organization/actions/blacklist/ActionsBlacklist->src/mixins/controls/organization/Action:ActionsSetPage", type);
    },
    setType() {
      this.$EBus.$emit("src/components/controls/organization/actions/blacklist/ActionsBlacklist->src/mixins/controls/organization/Action:ActionsSetType");
    }
  }
};
