export default {
  template: `
    <transition name="controls-fade">
        <div class="controls-modal" v-if="modalActive">
            <div class="controls-modal__main">
                <div class="controls-modal__main-confirmation">
                    <div class="controls-modal__main-content">
                        <div class="controls-modal__content-title">
                            <p>{{$GetText('Объявление в гос.волну')}}</p>
                        </div>
                        <div class="controls-modal__content-subtitle">
                            <p>{{$GetText('Введите сообщения, которые вы хотите')}} <br> {{$GetText('отправить в государственную волну')}}</p>
                        </div>
                        <input class="controls-modal__input" v-model="line1" :placeholder="$GetText('1 строка')" type="text">
                        <input class="controls-modal__input" v-model="line2" :placeholder="$GetText('2 строка')" type="text">
                        <input class="controls-modal__input" v-model="line3" :placeholder="$GetText('3 строка')" type="text">
                        <input class="controls-modal__input" v-model="line4" :placeholder="$GetText('4 строка')" type="text">
                        <input class="controls-modal__input" v-model="line5" :placeholder="$GetText('5 строка')" type="text">
                        <div class="controls-modal__content-buttons">
                            <div class="controls-modal__buttons-button" @click="addAction">{{$GetText('Отправить')}}</div>
                            <div class="controls-modal__buttons-button" @click="cancelAction">{{$GetText('Отменить')}}</div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </transition>
    `,
  data() {
    return {
      modalActive: false,
      line1: "",
      line2: "",
      line3: "",
      line4: "",
      line5: ""
    };
  },
  methods: {
    onMouseDown(e) {
      if (e.target.classList.contains("controls-modal")) this.closeModal();
    },
    closeModal() {
      this.modalActive = false;
      this.line1 = "";
      this.line2 = "";
      this.line3 = "";
      this.line4 = "";
      this.line5 = "";
    },
    cancelAction() {
      this.closeModal();
    },
    addAction() {
      let announcement = {
        line1: this.line1,
        line2: this.line2,
        line3: this.line3,
        line4: this.line4,
        line5: this.line5
      };
      this.$EBus.$emit("src/components/controls/organization/actions/ModalBlacklist->src/components/controls/organization/Modal:addAnnouncement", announcement);
      this.closeModal();
    }
  },
  mounted() {
    window.addEventListener("mousedown", this.onMouseDown);
    this.$EBus.$on("src/components/controls/organization/Modal->src/components/controls/organization/actions/ModalAnnouncement:ModalOpen", announcement => {
      if (announcement !== undefined) {
        Object.keys(announcement).forEach(key => this[key] = announcement[key]);
      }
      this.modalActive = true;
    });
  },
  destroyed() {
    this.$EBus.$off("src/components/controls/organization/Modal->src/components/controls/organization/actions/ModalAnnouncement:ModalOpen");
  }
};
