import exit from "./Assets/Icons/exit.vue";
import call from "./Assets/Icons/call.vue";
import message from "./Assets/Icons/message.vue";
import RentModal from "./Components/RentModal.vue";
import ListingBuyModal from "./Components/ListingBuyModal.vue";
import ListingCreateModal from "./Components/ListingCreateModal.vue";
import ExchangeAddModal from "./Components/ExchangeAddModal.vue";
import ExchangeBuyModal from "./Components/ExchangeBuyModal.vue";
import ListingDelete from "./Components/ListingDelete.vue";
import ListingContinue from "./Components/ListingContinue";
import ComplainShowModal from "./Components/ComplainShowModal.vue";
import ComplainAddModal from "./Components/ComplainAddModal.vue";
import ExchangeOfferDeleteModal from "./Components/ExchangeOfferDeleteModal.vue";
import StockUnloadModal from "./Components/StockUnloadModal.vue";
import StockPutModal from "./Components/StockPutModal.vue";
import StockNotifyModal from "./Components/StockNotifyModal.vue";
export default {
  props: {
    modalType: {
      type: String,
      required: true
    },
    modalData: {
      type: Object,
      required: true
    }
  },
  components: {
    exit,
    call,
    message,
    RentModal,
    ListingBuyModal,
    ListingCreateModal,
    ExchangeAddModal,
    ExchangeBuyModal,
    ListingDelete,
    ListingContinue,
    ComplainShowModal,
    ComplainAddModal,
    ExchangeOfferDeleteModal,
    StockUnloadModal,
    StockPutModal,
    StockNotifyModal
  },
  methods: {
    closeModal() {
      this.$store.commit("marketPlace/closeModal");
    }
  },
  computed: {
    isShowContact() {
      return this.modalData.data?.phoneNumber && !["listingContinue", "listingDelete", "complainAdd", "complainShow"].includes(this.modalType);
    },
    getModalTitle() {
      if (this.modalType === "listingCreate") {
        return "Створити оголошення";
      } else if (this.modalType === "transportRent") {
        return "Оренда транспорту";
      } else if (this.modalType === "listingBuy" && this.modalData.objectType === "car") {
        return "Купівля транспорту";
      } else if (this.modalType === "listingBuy" && ["apart", "house", "business"].includes(this.modalData.objectType)) {
        return "Купівля нерухомості";
      } else if (this.modalType === "listingBuy" && this.modalData.objectType === "clothes") {
        return "Купівля одягу";
      } else if (["exchangeAdd", "exchangeItemAdd"].includes(this.modalType)) {
        return "Виставити лот";
      } else if (this.modalType === "exchangeBuy") {
        return "Закупівля товарів";
      } else if (this.modalType === "listingDelete") {
        return "Видалення оголошення";
      } else if (this.modalType === "listingContinue") {
        return "Продовження часу оголошення";
      } else if (this.modalType === "complainShow") {
        return "Подробиці скарги";
      } else if (this.modalType === "complainAdd") {
        return "Поскаржитися на оголошення";
      } else if (this.modalType === "exchangeOfferDelete") {
        return "Видалити лот";
      } else if (this.modalType === "stockUnload") {
        return "Вивантажити зі складу";
      } else if (this.modalType === "stockNotify") {
        return "Неотримане майно";
      } else if (this.modalType === "stockPut") {
        return "Завантажити на склад";
      }
      return "";
    },
    getHintText() {
      const hints = {
        listingBuy: "Сума покупки округляється до цілого числа. Після купівлі, майно потрапляє на склад. Його необхідно забрати протягом 48 годин з моменту здійснення покупки. В іншому випадку майно відправиться на аукціон.",
        exchangeBuy: "Сума покупки округляється до цілого числа. Після купівлі, майно потрапляє на склад. Його необхідно забрати протягом 48 годин з моменту здійснення покупки. В іншому випадку майно відправиться на аукціон.",
        exchangeAdd: "Після розміщення лота, він перебуватиме на торговому майданчику 48 годин. Лот можна буде прибрати примусово, але за додаткову плату. Редагувати активний лот неможливо.",
        exchangeItemAdd: "Після розміщення лота, він перебуватиме на торговому майданчику 48 годин. Лот можна буде прибрати примусово, але за додаткову плату. Редагувати активний лот неможливо.",
        listingCreate: "Розміщення оголошень є платною послугою. Перш ніж оголошення стане доступним, необхідно вказати час дії вашого оголошення. Його можна буде продовжити пізніше. Вартість розміщення залежить від категорії майна.",
        transportRent: "Максимальний термін погодинної оренди - 24 години. Максимальний термін подобової оренди - 7 днів.",
        listingDelete: "У разі, якщо ви витратили кошти на продовження часу оголошення - під час видалення оголошення кошти не будуть повернуті.",
        listingContinue: "Максимальний період продовження часу оголошення - 120 годин."
      };
      return hints[this.modalType] || "";
    },
    getSelectedItem() {
      return this.$store.getters["marketPlace/getSelectedItem"];
    }
  }
};
