export default {
  template: `

    `,
  props: {
    sellPrice: Number
  },
  computed: {
    sum() {
      let sum = this.inputString ? this.sellPrice * +this.inputString : 0;
      return sum.toLocaleString();
    }
  },
  data() {
    return {
      inputString: ""
    };
  },
  methods: {
    sellChips() {
      if (this.inputString) {
        this.$emit("sellChips", +this.inputString);
        this.inputString = "";
      }
    }
  },
  watch: {
    inputString: function (val) {
      if (!/^[\d]+$/.test(val) || val.length > 20) {
        this.inputString = val.substring(0, val.length - 1);
      }
    }
  }
};
