import MPItemsCardList from "../../Components/MP-ItemsCardList/MP-ItemsCardList.vue";
import MPOpenedItem from "../../Components/MP-OpenedItem/MP-OpenedItem.vue";
export default {
  components: {
    MPItemsCardList,
    MPOpenedItem
  },
  computed: {
    getSelectedItem() {
      return this.$store.getters["marketPlace/getSelectedItem"];
    },
    getSearchItems() {
      return this.$store.getters["marketPlace/getSearchItems"];
    },
    getFiltersItems() {
      return this.$store.getters["marketPlace/getFiltersItems"](this.$route.params.section);
    },
    getItemCards() {
      if (this.$route.params.section === "favorites") {
        return this.$store.getters["marketPlace/getFavoritesItems"];
      } else if (this.$route.params.section === "myListings") {
        return this.getMyListings;
      } else if (this.getSearchItems?.length) {
        return this.getSearchItems;
      } else if (this.getFiltersItems?.length) {
        return this.getFiltersItems;
      } else {
        return this.$store.getters["marketPlace/getCardsList"](this.$route.params.section);
      }
    },
    getMyListings() {
      const myListings = this.$store.getters["marketPlace/getMyListings"];
      const myOffers = this.$store.getters["marketPlace/getMyOffersItems"];
      const itemsCard = [...myListings, ...myOffers].sort((a, b) => new Date(b.createdTime) - new Date(a.createdTime));
      return itemsCard;
    }
  }
};
