import Arrow from "./Components/Arrow.vue";
export default {
  components: {
    Arrow
  },
  data() {
    return {
      numberType: "",
      types: [{
        id: 1,
        default: 2500,
        donate: 800
      }, {
        id: 2,
        default: 5000,
        donate: 800
      }, {
        id: 3,
        default: 20000,
        donate: 800
      }],
      activeNumberPlate: {},
      isFirst: true,
      number: ""
    };
  },
  methods: {
    onKeyUp(e) {
      if (e.keyCode === 27 && !window.isModalShow) {
        if (!this.numberType) this.$router.push("/");else this.numberType = "";
      }
    },
    setType(type) {
      this.isFirst = true;
      this.number = "";
      this.numberType = type;
      this.activeNumberPlate = this.types[0];
    },
    changeNumberPlate(modifier) {
      this.isFirst = true;
      this.number = "";
      this.activeNumberPlate = this.types[this.activeNumberPlate.id - 1 + modifier] ? this.types[this.activeNumberPlate.id - 1 + modifier] : modifier > 0 ? this.types[0] : this.types[this.types.length - 1];
    },
    buyDefault(type) {
      this.$callClient("Client:Numbers:BuyDefault", this.activeNumberPlate.id, type);
    },
    setDefaultNumber(number) {
      this.number = number;
      this.isFirst = false;
    },
    takeDefaultNumber() {
      this.$callClient("Client:Numbers:TakeDefaultNumber");
    },
    buyDonate() {
      this.$callClient("Client:Numbers:BuyDonateNumber", this.activeNumberPlate.id, this.number);
    },
    onInput(e) {
      if (!/^[a-zA-Z0-9]+$/.test(e.target.value)) {
        e.target.value = e.target.value.substring(0, e.target.value.length - 1);
      }
      this.number = e.target.value;
    }
  },
  mounted() {
    window.addEventListener("keyup", this.onKeyUp);
    this.$onClient("Client:Numbers:SetDefaultNumber", this.setDefaultNumber);
    this.$callClient("Client:Numbers:Ready");
  },
  beforeUnmount() {
    window.removeEventListener("keyup", this.onKeyUp);
    this.$offClient("Client:Numbers:SetDefaultNumber", this.setDefaultNumber);
    this.$callClient("Client:Numbers:Destroyed");
  }
};
