export default {
  props: {
    playerinfo: Object
  },
  methods: {
    closeApp() {
      this.$callClient("C:FurnitureStore:Close");
    },
    formatNumberToCurrency(number) {
      let formattedNumber = number.toString().replace(".", ",");
      formattedNumber = formattedNumber.replace(/(\d)(?=(\d{3})+(?!\d))/g, "$1.");
      formattedNumber = formattedNumber + " $";
      return formattedNumber;
    }
  }
};
