import { mapState, mapGetters } from "vuex";
import DistributionCard from "./components/DistributionCard";
import OtherPlayer from "./components/OtherPlayer";
import CurrentPlayer from "./components/CurrentPlayer";
import SaleOfChips from "./components/SaleOfChips";
import AvatarOption from "./components/AvatarOption";
import ExitWindow from "./components/ExitWindow";
import InfoModal from "./components/InfoModal";
export default {
  name: "Poker",
  components: {
    DistributionCard,
    OtherPlayer,
    CurrentPlayer,
    SaleOfChips,
    AvatarOption,
    ExitWindow,
    InfoModal
  },
  data: function () {
    return {
      showSaleOfChips: false,
      showAvatarOption: false,
      showExitWindow: false,
      isShowHelpModal: false
    };
  },
  computed: {
    ...mapState("poker", ["cardsDistribution", "otherPlayers", "allBets", "ratesDone"]),
    winItem: function () {
      return this.cardsDistributionCombinations.forEach(item => item);
    }
  },
  methods: {
    exitDone() {
      this.setShowExitWindow(false);
      window.mp.trigger("poker:exit");
      this.setShowExitWindow(false);
    },
    setIsShowModal(data) {
      this.isShowHelpModal = data;
    },
    exitClose() {
      this.setShowExitWindow(false);
    },
    getPath(path) {
      return require(path);
    },
    setSaleOfChips: function (value) {
      this.showSaleOfChips = value;
    },
    setAvatarOption: function (value) {
      this.showAvatarOption = value;
    },
    setExitWindow: function (value) {
      this.showExitWindow = value;
    },
    onkeydown(e) {
      if (e.keyCode == 75) {
        const audio = document.getElementById("winningSound");
        audio.volume = 0.6;
        audio.play();
      }
    }
  },
  mounted() {
    window.addEventListener("keyup", this.onkeydown);
  }
};
