import CallIcon from "@/Application/Pages/Phone/Components/Icons/PhoneApp/CallIconInfo.vue";
import MsgIcon from "@/Application/Pages/Phone/Components/Icons/PhoneApp/MsgIcon.vue";
import LoadImg from "@/Application/Pages/Phone/Components/LoadImg.vue";
import { setNewMsgUI, setNewContact, closeAdditionalUi } from "../../Lib/additionalUiFunctions";
import PhoneMixin from "../../Mixins/PhoneMixin.vue";
export default {
  data() {
    return {
      isShowEmoji: false,
      emoji: "",
      testImg: ""
    };
  },
  props: {
    data: Object
  },
  mixins: [PhoneMixin],
  methods: {
    onClickImgPicker() {
      this.isShowEmoji = true;
    },
    onCloseImgPicker() {
      setTimeout(() => {
        this.isShowEmoji = false;
      });
    },
    setEmoji(emoji) {
      this.emoji = emoji;
    },
    callNumber() {
      this.$callClient("C:Phone:CallNumber", this.data.number);
    },
    addToFavorite() {
      this.$callClient("W:Phone:AddFavorite", this.data.id, !this.favorites.includes(this.data.id));
    },
    createNewMsg() {
      setNewMsgUI({
        number: this.data.number
      });
    },
    onBlock() {
      this.$callClient("W:Phone:BlockContact", this.data.number);
    },
    createNewContact() {
      setNewContact({
        number: this.data.number
      });
    },
    onBack() {
      closeAdditionalUi();
    },
    onRedact() {
      setNewContact({
        number: this.data.number,
        name: this.data.name,
        surname: this.data.surname,
        img: this.data.img,
        isRedact: true
      });
    },
    loadImg(img) {
      this.testImg = img;
    }
  },
  computed: {
    favorites() {
      return this.$store.getters["phone/getFavorites"];
    },
    blockedList() {
      return this.$store.getters["phone/getBlockedList"];
    },
    PMData() {
      return this.$store.getters["playerMenu/getMainData"];
    }
  },
  components: {
    CallIcon,
    MsgIcon,
    LoadImg
  },
  mounted() {}
};
