import { Howl } from "howler";
const sounds = {};
const settingsData = {
  volume: 0.5
};
export const initSettings = data => {
  settingsData.volume = data.volume;
};
const generateId = () => {
  return Math.random().toString(36).substring(7);
};
export const playSound = (sound, options = {}) => {
  const id = generateId();
  sounds[id] = new Howl({
    src: [sound],
    ...options,
    volume: settingsData.volume
  });
  sounds[id].play();
  return id;
};
export const stopSound = id => {
  if (sounds[id]) {
    sounds[id].stop();
    delete sounds[id];
  }
};
