import SystemAppHeader from "@/Application/Pages/Phone/Components/SystemAppHeader.vue";
import BlueArrowIcon from "../Components/BlueArrowIcon.vue";
import ArrowIcon from "../Components/ArrowIcon.vue";
import CallIcon from "../Components/CallIcon.vue";
import MsgItem from "../Components/MsgItem.vue";
import { setContactInfoItem } from "../../../../Lib/additionalUiFunctions";
import GalleryApp from "../../GalleryApp/GalleryApp.vue";
import PhoneMixin from "@/Application/Pages/Phone/Mixins/PhoneMixin";
export default {
  components: {
    SystemAppHeader,
    BlueArrowIcon,
    CallIcon,
    ArrowIcon,
    MsgItem,
    GalleryApp
  },
  data() {
    return {
      msg: "",
      isShowPhoto: false,
      isShowFunctional: false,
      imgList: []
    };
  },
  mixins: [PhoneMixin],
  watch: {
    msgInfo: {
      handler() {
        this.$store.commit("phone/setMsgRead", this.msgInfoNumber);
      },
      deep: true
    }
  },
  computed: {
    msgInfoNumber() {
      return this.$route.params.number;
    },
    msgInfo() {
      return this.$store.getters["phone/getMsgListByNumber"](this.msgInfoNumber);
    },
    numbers() {
      return this.$store.getters["phone/getNumbers"];
    },
    contactInfoItem() {
      return this.numbers[this.msgInfoNumber];
    }
  },
  methods: {
    onBack() {
      this.$router.go(-1);
    },
    goToContact() {
      setContactInfoItem(this.contactInfoItem || {
        number: this.msgInfoNumber
      });
    },
    onInput() {
      this.playSound(require("../../../../Assets/Sounds/System/tap.mp3"));
    },
    onSendPhoto(photo) {
      this.$callClient("W:Phone:SendPhoto", this.msgInfoNumber, photo);
      this.imgList = photo;
      this.isShowPhoto = false;
    },
    showPhoto() {
      this.isShowPhoto = true;
      this.isShowFunctional = false;
    },
    sendGeo() {
      this.$callClient("W:Phone:SendGeo", this.msgInfoNumber);
      this.isShowFunctional = false;
    },
    sendMsg() {
      this.$callClient("W:Phone:SendMsg", this.msgInfoNumber, this.msg);
      this.msg = "";
    },
    onCall() {
      this.$callClient("C:Phone:CallNumber", this.msgInfoNumber);
    },
    showFunctional() {
      this.isShowFunctional = true;
    }
  },
  beforeUnmount() {
    this.$store.dispatch("phone/setShownPhoto", "");
  },
  mounted() {
    setTimeout(() => {
      this.$refs.msgInput.focus();
    }, 100);
    this.$store.commit("phone/setMsgRead", this.msgInfoNumber);
  }
};
