import SystemAppHeader from "../../../../Components/SystemAppHeader.vue";
export default {
  data() {
    return {
      min: 0.8,
      max: 1.2,
      value: 1,
      firstValue: 1,
      isSaved: false
    };
  },
  components: {
    SystemAppHeader
  },
  computed: {
    gradient() {
      const value = (this.value - this.min) / (this.max - this.min) * 100;
      console.log(this.value);
      console.log(value);
      return `linear-gradient(to right, #0A84FF 0%, #0A84FF ${value}%, #1C1C1E ${value}%, #1C1C1E 100%)`;
    },
    settingsList() {
      return [{
        name: "Маленький",
        value: this.min
      }, {
        name: "Звичайний",
        value: 1
      }, {
        name: "Великий",
        value: this.max
      }];
    },
    getSettingsData() {
      return this.$store.getters["phone/getSettingsData"];
    }
  },
  watch: {
    value() {
      this.setPhoneSize(this.value);
    }
  },
  methods: {
    setPhoneSize(value) {
      const data = {
        phoneSize: value
      };
      this.$store.commit("phone/setPhoneSetting", data);
    },
    onBack() {
      this.$router.go(-1);
    },
    onSave() {
      this.$callClient("W:Phone:Settings:Save", "phoneSize", this.value);
      this.$router.go(-1);
      this.isSaved = true;
    }
  },
  mounted() {
    this.value = this.getSettingsData.phoneSize;
    this.firstValue = this.value;
  },
  beforeUnmount() {
    if (this.firstValue !== this.value && !this.isSaved) {
      this.setPhoneSize(this.firstValue);
    }
  }
};
