import MPSpecialBtn from "../MP-SpecialBtn/MP-SpecialBtn.vue";
export default {
  components: {
    MPSpecialBtn
  },
  methods: {
    playSound() {
      this.$refs.bellRing.play();
    },
    playAnim() {
      this.playSound();
      const bell = this.$refs.bell.$el;
      bell.classList.add("active-bell");
      setTimeout(() => {
        bell.classList.remove("active-bell");
      }, 300);
    }
  },
  computed: {
    getListLength() {
      return this.$store.getters["marketPlace/getCardsList"]("moderation")?.length || 0;
    }
  },
  watch: {
    getListLength(newVal, oldVal) {
      if (newVal > oldVal) {
        this.playAnim();
        this.playSound();
      }
    }
  }
};
