export default {
  namespaced: true,
  state: {
    universalModalList: [],
    formModalDataList: []
  },
  getters: {
    getUniversalModalList(state) {
      return state.universalModalList;
    },
    getFormModalDataByTitle: state => type => {
      console.log(state.formModalDataList);
      return state.formModalDataList[type];
    }
  },
  mutations: {
    addUniversalModal(state, universalModalData) {
      state.universalModalList.push(universalModalData);
      window.isModalShow = true;
    },
    addFormModalData(state, formModalData) {
      console.log(formModalData);
      state.formModalDataList[formModalData.name] = formModalData.value;
    },
    removeUniversalModal(state) {
      state.universalModalList = [];
      window.isModalShow = false;
    }
  },
  actions: {
    AddFormModalData({
      commit
    }, payload) {
